sqlMap.select:
SELECT * FROM (SELECT ROW_NUMBER() OVER( ORDER BY COMPONENT_ID ) rn , newtab.* FROM ( SELECT COMPONENT_ID COMPONENT_ID , ORG_ID ORG_ID , CODE CODE , NAME NAME, COMPONENT_TYPE_ID COMPONENT_TYPE_ID, REG_TIME REG_TIME , STATE STATE, STATE_TIME STATE_TIME, PASSWORD PASSWORD, DESCRIPTOR DESCRIPTOR FROM COMPONENT WHERE STATE NOT IN ('G','X')
COMPONENT_ID IN ($componentId$) tenant_id=#tenantId#
) newtab ) WHERE rn BETWEEN #pro# AND #end#
